import { Component } from "react"
import HeroVideo from "../HeroVideo/HeroVideo"
import VideoDescription from "../VideoDescription/VideoDescription"
import VideoList from "../VideoList/VideoList"
import DisplayComments from "../DisplayComments/DisplayComments"
import {API_URL, API_KEY} from "../../utils/utils"
// import details from "../../data/video-details.json"
import "../../App.scss"
import axios from "axios"

class MainPage extends Component {
    state = {
        videoList: [],
        currentVideo: [],
    }

    getVideoId = (videoId)=>{
        axios.get(`${API_URL}/videos/${videoId}/?api_key=${API_KEY}`)
            .then(res =>{
                this.setState({
                    currentVideo:res.data
                })
            })
            .catch(err =>{
                console.log(err)
            })
    }

    componentDidMount(){
        axios.get(`${API_URL}/videos/?api_key=${API_KEY}`)
            .then(res =>{
                this.setState({
                    videoList: res.data
                })
                this.getVideoId(this.state.videoList[0].id)
            })
            .catch(err =>{
                console.log(err)
            })
    }

    render() {
        
        if (this.state.videoList === [] || this.state.currentVideo === []) {
            return <div>Loading...</div>
        }

        return (
            <div>
                <HeroVideo
                    currentVideo={this.state.currentVideo}
                />
                <div className="App__divider-container">
                    <div className="App__main-content">
                        <VideoDescription
                            data={this.state.currentVideo}
                        />
                        <DisplayComments
                            data={this.state.currentVideo.comments}
                            preventDefault={this.preventDefault}
                        />
                    </div>
                    <div className="App__sidebar">
                        <VideoList
                            data={this.state.videoList}
                        />
                    </div>
                </div>
            </div>
        )
    }
}

export default MainPage




